import React, { useState, useEffect } from 'react';
import { Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';

const AddEmployeeComponent = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [emailId, setEmailId] = useState('');
    const history = useHistory();
    const { id } = useParams();

    useEffect(() => {
        if (id) {
            EmployeeService.getEmployeeById(id)
                .then((response) => {
                    setFirstName(response.data.firstName);
                    setLastName(response.data.lastName);
                    setEmailId(response.data.emailId);
                })
                .catch((error) => {
                    console.log(error);
                });
        }
    }, [id]);

    const saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        const employee = { firstName, lastName, emailId };

        if (id) {
            EmployeeService.updateEmployee(id, employee)
                .then(() => history.push('/employees'))
                .catch((error) => console.log(error));
        } else {
            EmployeeService.createEmployee(employee)
                .then(() => history.push('/employees'))
                .catch((error) => console.log(error));
        }
    };

    const modalTitle = id ? 'Update Employee' : 'Add Employee';

    return (
        <div className="modal-overlay">
            <div className="modal-content p-4 shadow-lg bg-white rounded">
                <h2 className="text-center text-primary mb-4">{modalTitle}</h2>
                <form>
                    <div className="form-group mb-3">
                        <label className="form-label">First Name:</label>
                        <input
                            type="text"
                            placeholder="Enter first name"
                            className="form-control"
                            value={firstName}
                            onChange={(e) => setFirstName(e.target.value)}
                        />
                    </div>

                    <div className="form-group mb-3">
                        <label className="form-label">Last Name:</label>
                        <input
                            type="text"
                            placeholder="Enter last name"
                            className="form-control"
                            value={lastName}
                            onChange={(e) => setLastName(e.target.value)}
                        />
                    </div>

                    <div className="form-group mb-4">
                        <label className="form-label">Email Id:</label>
                        <input
                            type="email"
                            placeholder="Enter email"
                            className="form-control"
                            value={emailId}
                            onChange={(e) => setEmailId(e.target.value)}
                        />
                    </div>

                    <div className="d-flex justify-content-between">
                        <button className="btn btn-success" onClick={saveOrUpdateEmployee}>
                            Submit
                        </button>
                        <Link to="/employees" className="btn btn-outline-danger">
                            Cancel
                        </Link>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AddEmployeeComponent;
