import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faTrash, faSearch } from '@fortawesome/free-solid-svg-icons';
import './ListEmployeeComponent.css';

const ListEmployeeComponent = () => {
    const [employees, setEmployees] = useState([]);
    const [filteredEmployees, setFilteredEmployees] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        getAllEmployees();
    }, []);

    const getAllEmployees = () => {
        EmployeeService.getAllEmployees()
            .then((response) => {
                setEmployees(response.data);
                setFilteredEmployees(response.data); // Initial filtered list = all
            })
            .catch((error) => console.log(error));
    };

    const deleteEmployee = (employeeId) => {
        EmployeeService.deleteEmployee(employeeId)
            .then(() => getAllEmployees())
            .catch((error) => console.log(error));
    };

    const handleSearch = (e) => {
        const value = e.target.value.toLowerCase();
        setSearchTerm(value);

        const filtered = employees.filter(emp =>
            emp.firstName.toLowerCase().includes(value) ||
            emp.lastName.toLowerCase().includes(value) ||
            emp.emailId.toLowerCase().includes(value)
        );

        setFilteredEmployees(filtered);
    };

    return (
        <div className="container mt-5">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2 className="text-gradient fw-bold">Employee Directory</h2>
                <Link to="/add-employee" className="btn btn-success fw-semibold shadow-sm">
                    + Add Employee
                </Link>
            </div>

            {/* Search Input */}
            <div className="input-group mb-4 shadow-sm">
                <span className="input-group-text bg-white">
                    <FontAwesomeIcon icon={faSearch} />
                </span>
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search by name or email..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </div>

            <div className="table-responsive rounded shadow-sm">
                <table className="table table-bordered align-middle">
                    <thead className="table-dark text-center">
                        <tr>
                            <th className="fw-semibold">ID</th>
                            <th className="fw-semibold">First Name</th>
                            <th className="fw-semibold">Last Name</th>
                            <th className="fw-semibold">Email</th>
                            <th className="fw-semibold">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredEmployees.length === 0 ? (
                            <tr>
                                <td colSpan="5" className="text-center py-4 text-muted">
                                    No matching employees found.
                                </td>
                            </tr>
                        ) : (
                            filteredEmployees.map((employee) => (
                                <tr key={employee.id}>
                                    <td className="text-center fw-bold">{employee.id}</td>
                                    <td className="fw-semibold">{employee.firstName}</td>
                                    <td className="fw-semibold">{employee.lastName}</td>
                                    <td className="fw-semibold">{employee.emailId}</td>
                                    <td className="text-center">
                                        <div className="btn-group">
                                            <Link
                                                to={`/edit-employee/${employee.id}`}
                                                className="btn btn-sm btn-primary me-2"
                                            >
                                                <FontAwesomeIcon icon={faPenToSquare} /> Update
                                            </Link>
                                            <button
                                                className="btn btn-sm btn-danger"
                                                onClick={() => deleteEmployee(employee.id)}
                                            >
                                                <FontAwesomeIcon icon={faTrash} /> Delete
                                            </button>
                                        </div>
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ListEmployeeComponent;
